function p(e){return/^(https?:|mailto:|tel:)/.test(e)}function g(e,r="YYYY-MM-DD HH:mm:ss"){if(!e)return"";const t=new Date(e),n=t.getFullYear(),a=String(t.getMonth()+1).padStart(2,"0"),s=String(t.getDate()).padStart(2,"0"),o=String(t.getHours()).padStart(2,"0"),c=String(t.getMinutes()).padStart(2,"0"),i=String(t.getSeconds()).padStart(2,"0");return r.replace("YYYY",n).replace("MM",a).replace("DD",s).replace("HH",o).replace("mm",c).replace("ss",i)}export{g as f,p as i};
